                Assert.Equal(CompressibilityUnit.InversePoundForcePerSquareInch, parsedUnit);
            }

            {
                Assert.True(Compressibility.TryParseUnit("1/psi", CultureInfo.GetCultureInfo("en-US"), out var parsedUnit));
                Assert.Equal(CompressibilityUnit.InversePoundForcePerSquareInch, parsedUnit);
            }

        }

