        [Theory]
        [MemberData(nameof(UnitTypes))]
        public void ToUnit(ElectricConductanceUnit unit)
        {
            var inBaseUnits = ElectricConductance.From(1.0, ElectricConductance.BaseUnit);
            var converted = inBaseUnits.ToUnit(unit);

