                Assert.True(Irradiance.TryParse("1 kW/m²", CultureInfo.GetCultureInfo("en-US"), out var parsed));
                Assert.Equal(1, parsed.KilowattsPerSquareMeter);
                Assert.Equal(IrradianceUnit.KilowattPerSquareMeter, parsed.Unit);
