        [Fact]
        public void DefaultCtor_ReturnsQuantityWithZeroValueAndBaseUnit()
        {
            var quantity = new Irradiation();
            Assert.Equal(0, quantity.Value);
            Assert.Equal(IrradiationUnit.JoulePerSquareMeter, quantity.Unit);
        }

        [Fact]
