        {
            var v = LinearPowerDensity.FromWattsPerMeter(1);
            Assert.True(v.Equals(LinearPowerDensity.FromWattsPerMeter(1), LinearPowerDensity.FromWattsPerMeter(0)));
            Assert.True(v.Equals(LinearPowerDensity.FromWattsPerMeter(1), LinearPowerDensity.FromWattsPerMeter(0.001m)));
