                (InformationUnit.Megabyte, InformationUnit.Bit) => new Information(_value * 8000000, InformationUnit.Bit),
                (InformationUnit.Pebibit, InformationUnit.Bit) => new Information(_value * 1125899906842624, InformationUnit.Bit),
